package figure;

public class Pentagono implements PoligonoRegolare {
	
	private final static double APOTEMA = 0.688;
	private final static int NUM_LATI = 5;
	
	private double lato;
	
	public Pentagono(double lato) {
		this.lato = lato;
	}

	@Override
	public double area() {
		return APOTEMA*lato;
	}
	
	@Override
	public double perimetro() {
		return lato*NUM_LATI;
	}

	@Override
	public void draw() {
		// TODO Auto-generated method stub
		
	}
	
}
